$color-blue: #007fff;
$color-yellow: #ffe300;
$color-black: #000000;
$color-white: #ffffff;
$color-gray: #cfcfcf;
$color-gray-dark: #979797;

$font-family-primary: 'Nunito',
sans-serif;

%button-yellow {
  width: 80px;
  height: 23px;
  font-size: 12px;
  font-weight: 800;
  line-height: 22px;
  text-align: center;
  text-decoration: none;
  color: $color-black;
  background-color: $color-yellow;
  border-radius: 14px;
  border: 1px solid $color-yellow;
  transition: background-color 0.2s linear,
    border-color 0.2s linear;
}

%button-yellow-hover {
  background-color: $color-black;
  color: $color-yellow;
}

html {
  scroll-behavior: smooth;
}

.wrapper *,
.wrapper *::before,
.wrapper *::after {
  box-sizing: border-box;
}

h1,
h2,
h3,
h4,
h5,
h6,
p {
  margin: 0;
}

ul,
li {
  list-style: none;
  margin: 0;
  padding: 0;
}

body {
  min-height: 100vh;
  font-family: $font-family-primary;
  box-sizing: border-box;
}

.header {
  width: 100%;
  padding-top: 100px;

  &-menu {}

  &-mobile__btn-div {
    visibility: hidden;
    opacity: 0;
  }

  &-list {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  &-item {
    display: block;

    &__link {
      width: 10%;
      height: 16px;
      color: $color-blue;
      font-size: 12px;
      font-weight: 800;
      line-height: 16px;
      text-transform: uppercase;
      letter-spacing: 3px;
      text-decoration: none;
    }
  }

  &-avatar {
    width: 52px;
    height: 52px;
    margin-top: 2px;
    margin-left: 19px;
  }
}

._item-avatar {
  width: 50%;
  display: flex;
  justify-content: center;
}

.img-avatar {
  user-select: none;
}

.container {
  max-width: 1100px;
  margin: 0 auto;
}


.main {}

// -- 01 --
.hello {
  padding-top: 120px;

  &__title {
    width: 50%;
    margin-left: 25%;
    font-size: 48px;
    font-weight: 700;
    line-height: 60px;
    text-align: center;
    letter-spacing: 1.3px;
  }

  &-list {
    display: flex;
    justify-content: center;
    padding-top: 50px;
  }

  &-item {
    display: block;
    margin: 0px 15px;

    &__link {}
  }
}

._blue {
  color: $color-blue;
}


.img-twitter {}

.img-behance {}

.img-instagram {}

// -- 02 --
.works {
  padding-top: 116px;

  &-list {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    list-style: none;
    margin: 0;
    padding: 0;
    align-items: flex-start;
  }

  &-item {
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    width: 520px;
    padding: 0 0 60px 60px;
    background-size: contain;
    background-repeat: no-repeat;

    &._ocean {
      background-image: url('../img/works-ocean.png');
      height: 505px;
    }

    &._mountains {
      background-image: url('../img/works-mountains.png');
      height: 737px;
    }

    &._doors {
      background-image: url('../img/works-doors.png');
      height: 667px;
      margin-top: -172px;
    }

    &._building {
      background-image: url('../img/works-building.png');
      height: 435px;
      margin-top: 60px;
    }

    &__button {
      @extend %button-yellow;
      padding: 0;
      margin-bottom: 20px;
    }

    &:hover {
      @extend %button-yellow-hover;
    }

    &__title {
      color: $color-white;
      font-size: 28px;
      font-weight: 700;
      line-height: 36px;
      letter-spacing: 0.7px;
    }
  }

}

._title-ocean {
  width: 65%;
}

._title-mountains {
  width: 75%;
}

._title-doors {
  width: 70%;
}

._title-building {
  width: 75%;
}

.img-ocean-mobile,
.img-mountains-mobile,
.img-doors-mobile,
.img-building-mobile {
  width: 0%;
  max-width: 0px;
  visibility: hidden;
}

// -- 03 --
.about {
  margin-top: 60px;

  &-list {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
  }

  &-item {
    display: block;
    width: 45%;
    margin-top: 60px;

    &__title {
      font-family: nunito;
      font-size: 28px;
      font-weight: 600;
      line-height: 30px;
    }

    &__content {
      font-family: Nunito;
      font-size: 16px;
      font-weight: 600;
      line-height: 30px;
      text-align: left;
      margin-top: 30px;
    }
  }

}

// -- 04 --
.gad {
  margin-top: 60px;
  margin-left: -7px;
}

.gad-list {
  display: flex;
}

.gad-item {
  display: block;
  width: 25%;
}

.gad-picture {}

.img-google {}

.img-airbnb {}

.img-dropbox {}

.img-google-mobile {
  width: 0%;
  max-width: 0px;
  visibility: hidden;
}

.img-airbnb-mobile {
  width: 0%;
  max-width: 0px;
  visibility: hidden;
}

.img-dropbox-mobile {
  width: 0%;
  max-width: 0px;
  visibility: hidden;
}

// -- 05 --
.links {
  margin-top: 82px;

  &-wrapper {
    display: flex;
    justify-content: space-between;
  }

  &-list {
    display: block;
    width: 47%;
  }

  &-item {
    display: block;
    margin-top: 42px;

    &__content {
      display: flex;
      justify-content: space-between;
    }
  }

}

._justify {
  display: block;
}

._gray-text {
  color: $color-gray-dark;
}

._year-mobile {
  visibility: hidden;
}

._year-desktop {
  visibility: visible;
}

// -- 06 --
.contacts {
  margin-top: 245px;
}

.row {
  display: flex;
  justify-content: space-between;
}

.column {}

.column._width-20proc {
  width: 20%;
}

.column._width-75proc {
  width: 75%;
  margin-top: 50px;
}

.contacts {
  &__title {
    font-family: nunito;
    font-size: 28px;
    font-weight: 600;
    line-height: 30px;
  }

  &__content {
    font-family: nunito;
    font-size: 16px;
    font-weight: 600;
    line-height: 30px;
    margin-top: 60px;
  }

  &-list {
    display: flex;
    padding-top: 30px;
  }

  &-item {
    display: block;
    margin: 0px 18px 0px 0px;
  }
}

._form-column {
  display: flex;
  flex-direction: column;
  width: 90%;
  margin-top: 3px;
}

.contact-form {

  &__label {
    display: flex;
    flex-direction: column-reverse;
    margin-top: 16px;
  }

  &__input {
    color: $color-black;
    font-family: nunito;
    font-size: 16px;
    font-weight: 600;
    line-height: 30px;
    border-top: none;
    border-right: none;
    border-left: none;
    border-bottom-color: $color-gray;
    margin-top: 5px;
  }

  &__input::-webkit-input-placeholder {
    color: $color-gray;
  }

  &__span {
    font-family: nunito;
    font-size: 12px;
    font-weight: 800;
    line-height: 16px;
    text-transform: uppercase;
    letter-spacing: 3px;
    margin-top: 20px;
    margin-bottom: 10px;
  }

  &__button {
    background: none;
    border: none;
    width: 52px;
    height: 52px;
    margin-top: 38px;
    margin-right: 6px;
  }
}

._message {
  height: 120px;
  border-bottom-width: 3px;
  resize: none;
}

.img-button {
  width: 52px;
  height: 52px;
}

.footer {
  margin-top: 121px;
  margin-bottom: 100px;

  &__content {
    font-family: Nunito;
    font-size: 16px;
    font-weight: 600;
    line-height: 30px;
  }

  &-buttons {
    width: 31%;
    display: flex;
    justify-content: space-between;

    &__button {
      color: $color-gray-dark;
      font-family: Nunito;
      font-size: 16px;
      font-weight: 600;
      line-height: 30px;
      text-decoration: none;
    }
  }
}
._invoicing {
  margin-right: 5px;
}
